exampleQueries.query:
query GetRepository($owner: String!, $name: String!) {
  repository(owner: $owner, name: $name) {
    name
    description
    stargazerCount
    forkCount
    primaryLanguage {
      name
      color
    }
    issues(first: 5, states: OPEN) {
      totalCount
      nodes {
        title
        createdAt
        author {
          login
        }
      }
    }
  }
}

query GetUser($login: String!) {
  user(login: $login) {
    name
    bio
    company
    location
    email
    websiteUrl
    followers {
      totalCount
    }
    following {
      totalCount
    }
    repositories(first: 5, orderBy: {field: STARGAZERS, direction: DESC}) {
      nodes {
        name
        description
        stargazerCount
        primaryLanguage {
          name
        }
      }
    }
  }
}

query SearchRepositories($query: String!, $first: Int!) {
  search(query: $query, type: REPOSITORY, first: $first) {
    repositoryCount
    nodes {
      ... on Repository {
        name
        owner {
          login
        }
        description
        stargazerCount
        forkCount
        primaryLanguage {
          name
        }
      }
    }
  }
}
